import React, { useState, useEffect } from 'react';
import { Plus, Edit, Trash2 } from 'lucide-react';
import axios from 'axios';
import '../css/AdminPanel.css';

const AdminPanel = () => {
    const [activeTab, setActiveTab] = useState('faqs'); // 'faqs' or 'meals'
    const [isOpen, setIsOpen] = useState(false);
    const [faqs, setFaqs] = useState([]);
    const [meals, setMeals] = useState([]);
    const [faqFormData, setFaqFormData] = useState({
        question: '',
        answer: '',
        id: null
    });
    const [mealFormData, setMealFormData] = useState({
        name: '',
        description: '',
        calories: '',
        image: '',
        category: 'Breakfast',
        id: null
    });
    const [isLoading, setIsLoading] = useState(false);
    const [isEditing, setIsEditing] = useState(false);

    const categories = ['Breakfast', 'Lunch', 'Dinner'];

    // FAQ Operations
    const fetchFAQs = async () => {
        try {
            const response = await axios.get('http://localhost:1240/faqs');
            setFaqs(response.data);
        } catch (error) {
            console.error('Error fetching FAQs:', error);
        }
    };

    // Meal Operations
    const fetchMeals = async () => {
        try {
            const response = await axios.get('http://localhost:1240/meals');
            setMeals(response.data);
        } catch (error) {
            console.error('Error fetching meals:', error);
        }
    };

    useEffect(() => {
        fetchFAQs();
        fetchMeals();
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        if (activeTab === 'faqs') {
            setFaqFormData(prev => ({
                ...prev,
                [name]: value
            }));
        } else {
            setMealFormData(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const handleEdit = (item) => {
        if (activeTab === 'faqs') {
            setFaqFormData({
                question: item.question,
                answer: item.answer,
                id: item._id
            });
        } else {
            setMealFormData({
                name: item.name,
                description: item.description,
                calories: item.calories,
                image: item.image,
                category: item.category,
                id: item._id
            });
        }
        setIsEditing(true);
        setIsOpen(true);
    };

    const handleDelete = async (id) => {
        const itemType = activeTab === 'faqs' ? 'FAQ' : 'meal';
        if (window.confirm(`Are you sure you want to delete this ${itemType}?`)) {
            try {
                if (activeTab === 'faqs') {
                    await axios.delete(`http://localhost:1240/delete-faq/${id}`);
                    fetchFAQs();
                } else {
                    await axios.delete(`http://localhost:1240/delete-meal/${id}`);
                    fetchMeals();
                }
            } catch (error) {
                console.error(`Error deleting ${itemType}:`, error);
            }
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);

        try {
            if (activeTab === 'faqs') {
                if (isEditing) {
                    await axios.put(`http://localhost:1240/update-faq/${faqFormData.id}`, faqFormData);
                } else {
                    await axios.post('http://localhost:1240/add-new-faq', faqFormData);
                }
                fetchFAQs();
                setFaqFormData({ question: '', answer: '', id: null });
            } else {
                if (isEditing) {
                    await axios.put(`http://localhost:1240/update-meal/${mealFormData.id}`, mealFormData);
                } else {
                    await axios.post('http://localhost:1240/add-new-meal', mealFormData);
                }
                fetchMeals();
                setMealFormData({ name: '', description: '', calories: '', image: '', category: 'Breakfast', id: null });
            }
            setIsOpen(false);
            setIsEditing(false);
        } catch (error) {
            console.error('Error saving:', error);
        } finally {
            setIsLoading(false);
        }
    };

    const handleModalClose = () => {
        setIsOpen(false);
        setIsEditing(false);
        if (activeTab === 'faqs') {
            setFaqFormData({ question: '', answer: '', id: null });
        } else {
            setMealFormData({ name: '', description: '', calories: '', image: '', category: 'Breakfast', id: null });
        }
    };

    return (
        <div className="admin-panel-container">
            <div className="admin-panel-tabs">
                <button
                    className={`admin-tab ${activeTab === 'faqs' ? 'active' : ''}`}
                    onClick={() => setActiveTab('faqs')}
                >
                    FAQ Management
                </button>
                <button
                    className={`admin-tab ${activeTab === 'meals' ? 'active' : ''}`}
                    onClick={() => setActiveTab('meals')}
                >
                    Meal Management
                </button>
            </div>

            <div className="admin-panel">
                <div className="admin-header">
                    <h2 className="admin-title">
                        {activeTab === 'faqs' ? 'FAQ Management' : 'Meal Management'}
                    </h2>
                    <button
                        onClick={() => setIsOpen(true)}
                        className="admin-add-button"
                    >
                        <Plus className="admin-icon" />
                        Add {activeTab === 'faqs' ? 'FAQ' : 'Meal'}
                    </button>
                </div>

                {activeTab === 'faqs' ? (
                    // FAQ List
                    faqs.length === 0 ? (
                        <p className="admin-empty">No FAQs added yet</p>
                    ) : (
                        <div className="admin-list">
                            {faqs.map((faq) => (
                                <div key={faq.id} className="admin-item">
                                    <div className="admin-content">
                                        <h3 className="admin-question">{faq.question}</h3>
                                        <p className="admin-answer">{faq.answer}</p>
                                    </div>
                                    <div className="admin-actions">
                                        <button
                                            onClick={() => handleEdit(faq)}
                                            className="admin-edit-button"
                                        >
                                            <Edit className="admin-icon" />
                                        </button>
                                        <button
                                            onClick={() => handleDelete(faq._id)}
                                            className="admin-delete-button"
                                        >
                                            <Trash2 className="admin-icon" />
                                        </button>
                                    </div>
                                </div>
                            ))}
                        </div>
                    )
                ) : (
                    // Meal List
                    meals.length === 0 ? (
                        <p className="admin-empty">No meals added yet</p>
                    ) : (
                        <div className="admin-list">
                            {meals.map((meal) => (
                                <div key={meal.id} className="admin-item">
                                    <div className="admin-content">
                                        <img 
                                            src={meal.image || "/api/placeholder/100/100"} 
                                            alt={meal.name}
                                            className="admin-meal-image"
                                        />
                                        <div className="admin-meal-details">
                                            <h3 className="admin-meal-name">{meal.name}</h3>
                                            <p className="admin-meal-description">{meal.description}</p>
                                            <div className="admin-meal-meta">
                                                <span className="admin-meal-calories">{meal.calories} calories</span>
                                                <span className="admin-meal-category">{meal.category}</span>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="admin-actions">
                                        <button
                                            onClick={() => handleEdit(meal)}
                                            className="admin-edit-button"
                                        >
                                            <Edit className="admin-icon" />
                                        </button>
                                        <button
                                            onClick={() => handleDelete(meal._id)}
                                            className="admin-delete-button"
                                        >
                                            <Trash2 className="admin-icon" />
                                        </button>
                                    </div>
                                </div>
                            ))}
                        </div>
                    )
                )}
            </div>

            {isOpen && (
                <div className="admin-modal-overlay">
                    <div className="admin-modal">
                        <div className="admin-modal-header">
                            <h3 className="admin-modal-title">
                                {isEditing ? `Edit ${activeTab === 'faqs' ? 'FAQ' : 'Meal'}` : 
                                           `Add New ${activeTab === 'faqs' ? 'FAQ' : 'Meal'}`}
                            </h3>
                            <button
                                onClick={handleModalClose}
                                className="admin-close-button"
                            >
                                Ã—
                            </button>
                        </div>

                        <form onSubmit={handleSubmit} className="admin-form">
                            {activeTab === 'faqs' ? (
                                // FAQ Form
                                <>
                                    <div className="admin-form-group">
                                        <label>Question</label>
                                        <input
                                            type="text"
                                            name="question"
                                            value={faqFormData.question}
                                            onChange={handleInputChange}
                                            placeholder="Enter FAQ question"
                                            required
                                        />
                                    </div>
                                    <div className="admin-form-group">
                                        <label>Answer</label>
                                        <textarea
                                            name="answer"
                                            value={faqFormData.answer}
                                            onChange={handleInputChange}
                                            placeholder="Enter FAQ answer"
                                            required
                                            rows={4}
                                        />
                                    </div>
                                </>
                            ) : (
                                // Meal Form
                                <>
                                    <div className="admin-form-group">
                                        <label>Name</label>
                                        <input
                                            type="text"
                                            name="name"
                                            value={mealFormData.name}
                                            onChange={handleInputChange}
                                            placeholder="Enter meal name"
                                            required
                                        />
                                    </div>
                                    <div className="admin-form-group">
                                        <label>Description</label>
                                        <textarea
                                            name="description"
                                            value={mealFormData.description}
                                            onChange={handleInputChange}
                                            placeholder="Enter meal description"
                                            required
                                            rows={3}
                                        />
                                    </div>
                                    <div className="admin-form-group">
                                        <label>Calories</label>
                                        <input
                                            type="number"
                                            name="calories"
                                            value={mealFormData.calories}
                                            onChange={handleInputChange}
                                            placeholder="Enter calories"
                                            required
                                            min="0"
                                        />
                                    </div>
                                    <div className="admin-form-group">
                                        <label>Image URL</label>
                                        <input
                                            type="text"
                                            name="image"
                                            value={mealFormData.image}
                                            onChange={handleInputChange}
                                            placeholder="Enter image URL"
                                        />
                                    </div>
                                    <div className="admin-form-group">
                                        <label>Category</label>
                                        <select
                                            name="category"
                                            value={mealFormData.category}
                                            onChange={handleInputChange}
                                            required
                                        >
                                            {categories.map((category) => (
                                                <option key={category} value={category}>
                                                    {category}
                                                </option>
                                            ))}
                                        </select>
                                    </div>
                                </>
                            )}
                            <div className="admin-form-actions">
                                <button
                                    type="button"
                                    onClick={handleModalClose}
                                    className="admin-cancel-button"
                                >
                                    Cancel
                                </button>
                                <button
                                    type="submit"
                                    disabled={isLoading}
                                    className="admin-submit-button"
                                >
                                    {isLoading ? 'Saving...' : (isEditing ? 'Save Changes' : `Add ${activeTab === 'faqs' ? 'FAQ' : 'Meal'}`)}
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AdminPanel;
