import React, { useState, useEffect } from 'react';
import { Plus, Edit, Trash2 } from 'lucide-react';
import axios from 'axios';
import '../css/AdminPanel.css';

const AdminPanel = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [faqs, setFaqs] = useState([]);
    const [formData, setFormData] = useState({
        question: '',
        answer: '',
        id: null
    });
    const [isLoading, setIsLoading] = useState(false);
    const [isEditing, setIsEditing] = useState(false);

    const fetchFAQs = async () => {
        try {
            const response = await axios.get('http://localhost:1240/faqs');
            setFaqs(response.data);
        } catch (error) {
            console.error('Error fetching FAQs:', error);
        }
    };

    useEffect(() => {
        fetchFAQs();
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleEdit = (faq) => {
        setFormData({
            question: faq.question,
            answer: faq.answer,
            id: faq.id
        });
        setIsEditing(true);
        setIsOpen(true);
    };

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this FAQ?')) {
            try {
                await axios.delete(`http://localhost:1240/delete-faq/${id}`);
                fetchFAQs();
            } catch (error) {
                console.error('Error deleting FAQ:', error);
            }
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);

        try {
            if (isEditing) {
                await axios.put(`http://localhost:1240/update-faq/${formData.id}`, formData);
            } else {
                await axios.post('http://localhost:1240/add-new-faq', formData);
            }
            fetchFAQs();
            setFormData({ question: '', answer: '', id: null });
            setIsOpen(false);
            setIsEditing(false);
        } catch (error) {
            console.error('Error saving FAQ:', error);
        } finally {
            setIsLoading(false);
        }
    };

    const handleModalClose = () => {
        setIsOpen(false);
        setIsEditing(false);
        setFormData({ question: '', answer: '', id: null });
    };

    return (
        <div className="admin-container">
            <div className="admin-panel">
                <div className="panel-header">
                    <h2 className="panel-title">FAQ Management</h2>
                    <button
                        onClick={() => setIsOpen(true)}
                        className="add-button"
                    >
                        <Plus className="icon" />
                        Add FAQ
                    </button>
                </div>

                {faqs.length === 0 ? (
                    <p className="no-faqs">No FAQs added yet</p>
                ) : (
                    <div className="faq-list">
                        {faqs.map((faq) => (
                            <div key={faq.id} className="faq-item">
                                <div className="faq-content">
                                    <h3 className="faq-question">{faq.question}</h3>
                                    <p className="faq-answer">{faq.answer}</p>
                                </div>
                                <div className="faq-actions">
                                    <button
                                        onClick={() => handleEdit(faq)}
                                        className="edit-button"
                                    >
                                        <Edit className="icon" />
                                    </button>
                                    <button
                                        onClick={() => handleDelete(faq.id)}
                                        className="delete-button"
                                    >
                                        <Trash2 className="icon" />
                                    </button>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>

            {isOpen && (
                <div className="modal-overlay">
                    <div className="modal">
                        <div className="modal-header">
                            <h3 className="modal-title">
                                {isEditing ? 'Edit FAQ' : 'Add New FAQ'}
                            </h3>
                            <button
                                onClick={handleModalClose}
                                className="close-button"
                            >
                                Ã—
                            </button>
                        </div>

                        <form onSubmit={handleSubmit} className="modal-form">
                            <div className="form-group">
                                <label>Question</label>
                                <input
                                    type="text"
                                    name="question"
                                    value={formData.question}
                                    onChange={handleInputChange}
                                    placeholder="Enter FAQ question"
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label>Answer</label>
                                <textarea
                                    name="answer"
                                    value={formData.answer}
                                    onChange={handleInputChange}
                                    placeholder="Enter FAQ answer"
                                    required
                                    rows={4}
                                />
                            </div>
                            <div className="form-actions">
                                <button
                                    type="button"
                                    onClick={handleModalClose}
                                    className="cancel-button"
                                >
                                    Cancel
                                </button>
                                <button
                                    type="submit"
                                    disabled={isLoading}
                                    className="submit-button"
                                >
                                    {isLoading ? 'Saving...' : (isEditing ? 'Save Changes' : 'Add FAQ')}
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AdminPanel;
