import React, { useEffect, useState } from 'react';
import axios from 'axios';

function GovtIssues() {
  const [issues, setIssues] = useState([]);

  useEffect(() => {
    axios.get('/api/issues').then((response) => {
      setIssues(response.data);
    });
  }, []);

  const handleUpvote = (id) => {
    axios.patch(`/api/issues/${id}/vote`, { voteType: 'upvote' }).then(() => {
      setIssues((prev) =>
        prev.map((issue) =>
          issue.id === id ? { ...issue, upvotes: issue.upvotes + 1 } : issue
        )
      );
    });
  };

  const handleDownvote = (id) => {
    axios.patch(`/api/issues/${id}/vote`, { voteType: 'downvote' }).then(() => {
      setIssues((prev) =>
        prev.map((issue) =>
          issue.id === id ? { ...issue, downvotes: issue.downvotes + 1 } : issue
        )
      );
    });
  };

  const handleAddComment = (id, comment) => {
    axios.post(`/api/issues/${id}/comment`, { comment }).then(() => {
      setIssues((prev) =>
        prev.map((issue) =>
          issue.id === id
            ? { ...issue, comments: [...(issue.comments || []), comment] }
            : issue
        )
      );
    });
  };

  return (
    <div className="govt-issues">
      <h1>Government Issues</h1>
      <div>
        {issues.map((issue) => (
          <div key={issue.id} className="issue-card">
            <h4>{issue.title}</h4>
            <p>{issue.description}</p>
            <button onClick={() => handleUpvote(issue.id)}>Upvotes: {issue.upvotes}</button>
            <button onClick={() => handleDownvote(issue.id)}>Downvotes: {issue.downvotes}</button>
            <button onClick={() => handleAddComment(issue.id, 'New Comment')}>Comments: {issue.comments?.length || 0}</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default GovtIssues;
