import React, { useState, useEffect } from 'react';
import { Plus, Edit, Trash2 } from 'lucide-react';
import axios from 'axios';


const MealAdminPanel = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [meals, setMeals] = useState([]);
    const [formData, setFormData] = useState({
        name: '',
        description: '',
        calories: '',
        image: '',
        category: 'Breakfast',
        id: null
    });
    const [isLoading, setIsLoading] = useState(false);
    const [isEditing, setIsEditing] = useState(false);

    const categories = ['Breakfast', 'Lunch', 'Dinner'];

    const fetchMeals = async () => {
        try {
            const response = await axios.get('http://localhost:1240/meals');
            setMeals(response.data);
        } catch (error) {
            console.error('Error fetching meals:', error);
        }
    };

    useEffect(() => {
        fetchMeals();
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleEdit = (meal) => {
        setFormData({
            name: meal.name,
            description: meal.description,
            calories: meal.calories,
            image: meal.image,
            category: meal.category,
            id: meal._id
        });
        setIsEditing(true);
        setIsOpen(true);
    };

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this meal?')) {
            try {
                await axios.delete(`http://localhost:1240/delete-meal/${id}`);
                fetchMeals();
            } catch (error) {
                console.error('Error deleting meal:', error);
            }
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);

        try {
            if (isEditing) {
                await axios.put(`http://localhost:1240/update-meal/${formData.id}`, formData);
            } else {
                await axios.post('http://localhost:1240/add-new-meal', formData);
            }
            fetchMeals();
            setFormData({ name: '', description: '', calories: '', image: '', category: 'Breakfast', id: null });
            setIsOpen(false);
            setIsEditing(false);
        } catch (error) {
            console.error('Error saving meal:', error);
        } finally {
            setIsLoading(false);
        }
    };

    const handleModalClose = () => {
        setIsOpen(false);
        setIsEditing(false);
        setFormData({ name: '', description: '', calories: '', image: '', category: 'Breakfast', id: null });
    };

    return (
        <div className="admin-meal-container">
            <div className="admin-meal-panel">
                <div className="admin-meal-header">
                    <h2 className="admin-meal-title">Meal Management</h2>
                    <button
                        onClick={() => setIsOpen(true)}
                        className="admin-meal-add-button"
                    >
                        <Plus className="admin-meal-icon" />
                        Add Meal
                    </button>
                </div>

                {meals.length === 0 ? (
                    <p className="admin-meal-empty">No meals added yet</p>
                ) : (
                    <div className="admin-meal-list">
                        {meals.map((meal) => (
                            <div key={meal.id} className="admin-meal-item">
                                <div className="admin-meal-content">
                                    <img 
                                        src={meal.image || "/api/placeholder/100/100"} 
                                        alt={meal.name}
                                        className="admin-meal-image"
                                    />
                                    <div className="admin-meal-details">
                                        <h3 className="admin-meal-name">{meal.name}</h3>
                                        <p className="admin-meal-description">{meal.description}</p>
                                        <div className="admin-meal-meta">
                                            <span className="admin-meal-calories">{meal.calories} calories</span>
                                            <span className="admin-meal-category">{meal.category}</span>
                                        </div>
                                    </div>
                                </div>
                                <div className="admin-meal-actions">
                                    <button
                                        onClick={() => handleEdit(meal)}
                                        className="admin-meal-edit-button"
                                    >
                                        <Edit className="admin-meal-icon" />
                                    </button>
                                    <button
                                        onClick={() => handleDelete(meal._id)}
                                        className="admin-meal-delete-button"
                                    >
                                        <Trash2 className="admin-meal-icon" />
                                    </button>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>

            {isOpen && (
                <div className="admin-meal-modal-overlay">
                    <div className="admin-meal-modal">
                        <div className="admin-meal-modal-header">
                            <h3 className="admin-meal-modal-title">
                                {isEditing ? 'Edit Meal' : 'Add New Meal'}
                            </h3>
                            <button
                                onClick={handleModalClose}
                                className="admin-meal-close-button"
                            >
                                ×
                            </button>
                        </div>

                        <form onSubmit={handleSubmit} className="admin-meal-form">
                            <div className="admin-meal-form-group">
                                <label>Name</label>
                                <input
                                    type="text"
                                    name="name"
                                    value={formData.name}
                                    onChange={handleInputChange}
                                    placeholder="Enter meal name"
                                    required
                                />
                            </div>
                            <div className="admin-meal-form-group">
                                <label>Description</label>
                                <textarea
                                    name="description"
                                    value={formData.description}
                                    onChange={handleInputChange}
                                    placeholder="Enter meal description"
                                    required
                                    rows={3}
                                />
                            </div>
                            <div className="admin-meal-form-group">
                                <label>Calories</label>
                                <input
                                    type="number"
                                    name="calories"
                                    value={formData.calories}
                                    onChange={handleInputChange}
                                    placeholder="Enter calories"
                                    required
                                    min="0"
                                />
                            </div>
                            <div className="admin-meal-form-group">
                                <label>Image URL</label>
                                <input
                                    type="text"
                                    name="image"
                                    value={formData.image}
                                    onChange={handleInputChange}
                                    placeholder="Enter image URL"
                                />
                            </div>
                            <div className="admin-meal-form-group">
                                <label>Category</label>
                                <select
                                    name="category"
                                    value={formData.category}
                                    onChange={handleInputChange}
                                    required
                                >
                                    {categories.map((category) => (
                                        <option key={category} value={category}>
                                            {category}
                                        </option>
                                    ))}
                                </select>
                            </div>
                            <div className="admin-meal-form-actions">
                                <button
                                    type="button"
                                    onClick={handleModalClose}
                                    className="admin-meal-cancel-button"
                                >
                                    Cancel
                                </button>
                                <button
                                    type="submit"
                                    disabled={isLoading}
                                    className="admin-meal-submit-button"
                                >
                                    {isLoading ? 'Saving...' : (isEditing ? 'Save Changes' : 'Add Meal')}
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default MealAdminPanel;
