import React from 'react';

const RECIPES = [

  {
    id: 2,
    name: "Butternut Squash Risotto",
    time: "45 minutes",
    difficulty: "Medium",
    ingredients: [
      "Arborio rice",
      "Butternut squash",
      "White wine",
      "Vegetable stock",
      "Parmesan cheese",
      "Sage leaves"
    ],
    instructions: "Roast squash, gradually add stock to rice, finish with parmesan and crispy sage."
  },
  {
    id: 3,
    name: "Dark Chocolate Lava Cakes",
    time: "30 minutes",
    difficulty: "Hard",
    ingredients: [
      "Dark chocolate",
      "Butter",
      "Eggs",
      "Sugar",
      "Flour",
      "Vanilla extract"
    ],
    instructions: "Melt chocolate and butter, mix with other ingredients, bake until edges are set but center is gooey."
  }
];

const styles = {
  container: {
    maxWidth: '800px',
    margin: '2rem auto',
    padding: '2rem',
    backgroundColor: '#fff',
    borderRadius: '12px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif'
  },
  header: {
    textAlign: 'center',
    marginBottom: '2rem',
  },
  date: {
    color: '#666',
    fontSize: '0.9rem',
    marginBottom: '0.5rem',
  },
  title: {
    fontSize: '2.5rem',
    color: '#2c3e50',
    marginBottom: '0.5rem',
  },
  recipeCard: {
    backgroundColor: '#f8f9fa',
    padding: '2rem',
    borderRadius: '8px',
  },
  recipeName: {
    fontSize: '1.8rem',
    color: '#e67e22',
    marginBottom: '1rem',
  },
  metadata: {
    display: 'flex',
    gap: '2rem',
    marginBottom: '1.5rem',
    color: '#666',
  },
  section: {
    marginBottom: '1.5rem',
  },
  sectionTitle: {
    fontSize: '1.2rem',
    color: '#2c3e50',
    marginBottom: '0.5rem',
    fontWeight: 'bold',
  },
  list: {
    listStyle: 'none',
    padding: 0,
    margin: 0,
  },
  listItem: {
    padding: '0.3rem 0',
    borderBottom: '1px solid #eee',
  },
  instructions: {
    lineHeight: '1.6',
    color: '#444',
  }
};

const RecipeOfTheDay = () => {
  // Use current date as seed for "random" selection
  const today = new Date();
  const seed = today.getFullYear() * 10000 + 
               (today.getMonth() + 1) * 100 + 
               today.getDate();
  const recipeIndex = seed % RECIPES.length;
  const recipe = RECIPES[recipeIndex];

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <div style={styles.date}>
          {today.toLocaleDateString('en-US', { 
            weekday: 'long',
            year: 'numeric',
            month: 'long',
            day: 'numeric'
          })}
        </div>
        <h1 style={styles.title}>Recipe of the Day</h1>
      </header>

      <div style={styles.recipeCard}>
        <h2 style={styles.recipeName}>{recipe.name}</h2>

        <div style={styles.metadata}>
          <span>⏱️ {recipe.time}</span>
          <span>📊 {recipe.difficulty}</span>
        </div>

        <div style={styles.section}>
          <h3 style={styles.sectionTitle}>Ingredients</h3>
          <ul style={styles.list}>
            {recipe.ingredients.map((ingredient, index) => (
              <li key={index} style={styles.listItem}>{ingredient}</li>
            ))}
          </ul>
        </div>

        <div style={styles.section}>
          <h3 style={styles.sectionTitle}>Instructions</h3>
          <p style={styles.instructions}>{recipe.instructions}</p>
        </div>
      </div>
    </div>
  );
};

export default RecipeOfTheDay;
