import React, { useState, useEffect } from 'react';
import { Plus, Edit, Trash2 } from 'lucide-react';
import axios from 'axios';
import '../css/AdminPanel.css';

const AdminPanel = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [isEditing, setIsEditing] = useState(false);
    const [faqs, setFaqs] = useState([]);
    const [meals, setMeals] = useState([]);
    const [formData, setFormData] = useState({
        question: '',
        answer: '',
        id: null
    });
    const [mealData, setMealData] = useState({
        name: '',
        description: '',
        calories: '',
        imageLink: '',
        category: 'Breakfast',
        id: null
    });
    const [isLoading, setIsLoading] = useState(false);
    const [isMealOpen, setIsMealOpen] = useState(false);
    const [isMealEditing, setIsMealEditing] = useState(false);

    const fetchFAQs = async () => {
        try {
            const response = await axios.get('http://localhost:1240/faqs');
            setFaqs(response.data);
        } catch (error) {
            console.error('Error fetching FAQs:', error);
        }
    };

    const fetchMeals = async () => {
        try {
            const response = await axios.get('http://localhost:1240/meals');
            setMeals(response.data);
        } catch (error) {
            console.error('Error fetching meals:', error);
        }
    };

    useEffect(() => {
        fetchFAQs();
        fetchMeals();
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleMealInputChange = (e) => {
        const { name, value } = e.target;
        setMealData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleEdit = (faq) => {
        setFormData({
            question: faq.question,
            answer: faq.answer,
            id: faq._id
        });
        setIsEditing(true);
        setIsOpen(true);
    };

    const handleMealEdit = (meal) => {
        setMealData({
            name: meal.name,
            description: meal.description,
            calories: meal.calories,
            imageLink: meal.imageLink,
            category: meal.category,
            id: meal._id
        });
        setIsMealEditing(true);
        setIsMealOpen(true);
    };

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this FAQ?')) {
            try {
                console.log('Deleting FAQ with ID:', id);
                await axios.delete(`http://localhost:1240/delete-faq/${id}`);
                fetchFAQs();
            } catch (error) {
                console.error('Error deleting FAQ:', error);
            }
        }
    };

    const handleMealDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this meal?')) {
            try {
                console.log('Deleting meal with ID:', id);
                await axios.delete(`http://localhost:1240/delete-meal/${id}`);
                fetchMeals();
            } catch (error) {
                console.error('Error deleting meal:', error);
            }
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);

        try {
            if (isEditing) {
                await axios.put(`http://localhost:1240/update-faq/${formData.id}`, formData);
            } else {
                await axios.post('http://localhost:1240/add-new-faq', formData);
            }
            fetchFAQs();
            setFormData({ question: '', answer: '', id: null });
            setIsOpen(false);
            setIsEditing(false);
        } catch (error) {
            console.error('Error saving FAQ:', error);
        } finally {
            setIsLoading(false);
        }
    };

    const handleMealSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);

        try {
            if (isMealEditing) {
                await axios.put(`http://localhost:1240/update-meal/${mealData.id}`, mealData);
            } else {
                await axios.post('http://localhost:1240/add-new-meal', mealData);
            }
            fetchMeals();
            setMealData({ name: '', description: '', calories: '', imageLink: '', category: 'Breakfast', id: null });
            setIsMealOpen(false);
            setIsMealEditing(false);
        } catch (error) {
            console.error('Error saving meal:', error);
        } finally {
            setIsLoading(false);
        }
    };

    const handleModalClose = () => {
        setIsOpen(false);
        setIsEditing(false);
        setFormData({ question: '', answer: '', id: null });
    };

    const handleMealModalClose = () => {
        setIsMealOpen(false);
        setIsMealEditing(false);
        setMealData({ name: '', description: '', calories: '', imageLink: '', category: 'Breakfast', id: null });
    };

    return (
        <div className="admin-faq-container">
            <div className="admin-faq-panel">
                <div className="admin-faq-header">
                    <h2 className="admin-faq-title">FAQ Management</h2>
                    <button
                        onClick={() => setIsOpen(true)}
                        className="admin-faq-add-button"
                    >
                        <Plus className="admin-faq-icon" />
                        Add FAQ
                    </button>
                </div>

                {faqs.length === 0 ? (
                    <p className="admin-faq-empty">No FAQs added yet</p>
                ) : (
                    <div className="admin-faq-list">
                        {faqs.map((faq) => (
                            <div key={faq._id} className="admin-faq-item">
                                <div className="admin-faq-content">
                                    <h3 className="admin-faq-question">{faq.question}</h3>
                                    <p className="admin-faq-answer">{faq.answer}</p>
                                </div>
                                <div className="admin-faq-actions">
                                    <button
                                        onClick={() => handleEdit(faq)}
                                        className="admin-faq-edit-button"
                                    >
                                        <Edit className="admin-faq-icon" />
                                    </button>
                                    <button
                                        onClick={() => handleDelete(faq._id)}
                                        className="admin-faq-delete-button"
                                    >
                                        <Trash2 className="admin-faq-icon" />
                                    </button>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>

            <div className="admin-meal-panel">
                <div className="admin-meal-header">
                    <h2 className="admin-meal-title">Meal Management</h2>
                    <button
                        onClick={() => setIsMealOpen(true)}
                        className="admin-meal-add-button"
                    >
                        <Plus className="admin-meal-icon" />
                        Add Meal
                    </button>
                </div>

                {meals.length === 0 ? (
                    <p className="admin-meal-empty">No meals added yet</p>
                ) : (
                    <div className="admin-meal-list">
                        {meals.map((meal) => (
                            <div key={meal._id} className="admin-meal-item">
                                <div className="admin-meal-content">
                                    <h3 className="admin-meal-name">{meal.name}</h3>
                                    <p className="admin-meal-description">{meal.description}</p>
                                    <p className="admin-meal-calories">{meal.calories} calories</p>
                                    <img src={meal.imageLink} alt={meal.name} className="admin-meal-image" />
                                    <p className="admin-meal-category">{meal.category}</p>
                                </div>
                                <div className="admin-meal-actions">
                                    <button
                                        onClick={() => handleMealEdit(meal)}
                                        className="admin-meal-edit-button"
                                    >
                                        <Edit className="admin-meal-icon" />
                                    </button>
                                    <button
                                        onClick={() => handleMealDelete(meal._id)}
                                        className="admin-meal-delete-button"
                                    >
                                        <Trash2 className="admin-meal-icon" />
                                    </button>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>

            {isOpen && (
                <div className="admin-faq-modal-overlay">
                    <div className="admin-faq-modal">
                        <div className="admin-faq-modal-header">
                            <h3 className="admin-faq-modal-title">
                                {isEditing ? 'Edit FAQ' : 'Add New FAQ'}
                            </h3>
                            <button
                                onClick={handleModalClose}
                                className="admin-faq-close-button"
                            >
                                ×
                            </button>
                        </div>

                        <form onSubmit={handleSubmit} className="admin-faq-form">
                            <div className="admin-faq-form-group">
                                <label>Question</label>
                                <input
                                    type="text"
                                    name="question"
                                    value={formData.question}
                                    onChange={handleInputChange}
                                    placeholder="Enter FAQ question"
                                    required
                                />
                            </div>
                            <div className="admin-faq-form-group">
                                <label>Answer</label>
                                <textarea
                                    name="answer"
                                    value={formData.answer}
                                    onChange={handleInputChange}
                                    placeholder="Enter FAQ answer"
                                    required
                                    rows={4}
                                />
                            </div>
                            <div className="admin-faq-form-actions">
                                <button
                                    type="button"
                                    onClick={handleModalClose}
                                    className="admin-faq-cancel-button"
                                >
                                    Cancel
                                </button>
                                <button
                                    type="submit"
                                    disabled={isLoading}
                                    className="admin-faq-submit-button"
                                >
                                    {isLoading ? 'Saving...' : (isEditing ? 'Save Changes' : 'Add FAQ')}
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}

            {isMealOpen && (
                <div className="admin-meal-modal-overlay">
                    <div className="admin-meal-modal">
                        <div className="admin-meal-modal-header">
                            <h3 className="admin-meal-modal-title">
                                {isMealEditing ? 'Edit Meal' : 'Add New Meal'}
                            </h3>
                            <button
                                onClick={handleMealModalClose}
                                className="admin-meal-close-button"
                            >
                                ×
                            </button>
                        </div>

                        <form onSubmit={handleMealSubmit} className="admin-meal-form">
                            <div className="admin-meal-form-group">
                                <label>Name</label>
                                <input
                                    type="text"
                                    name="name"
                                    value={mealData.name}
                                    onChange={handleMealInputChange}
                                    placeholder="Enter meal name"
                                    required
                                />
                            </div>
                            <div className="admin-meal-form-group">
                                <label>Description</label>
                                <textarea
                                    name="description"
                                    value={mealData.description}
                                    onChange={handleMealInputChange}
                                    placeholder="Enter meal description"
                                    required
                                    rows={4}
                                />
                            </div>
                            <div className="admin-meal-form-group">
                                <label>Calories</label>
                                <input
                                    type="number"
                                    name="calories"
                                    value={mealData.calories}
                                    onChange={handleMealInputChange}
                                    placeholder="Enter calories"
                                    required
                                />
                            </div>
                            <div className="admin-meal-form-group">
                                <label>Image Link</label>
                                <input
                                    type="text"
                                    name="imageLink"
                                    value={mealData.imageLink}
                                    onChange={handleMealInputChange}
                                    placeholder="Enter image link"
                                    required
                                />
                            </div>
                            <div className="admin-meal-form-group">
                                <label>Category</label>
                                <select
                                    name="category"
                                    value={mealData.category}
                                    onChange={handleMealInputChange}
                                    required
                                >
                                    <option value="Breakfast">Breakfast</option>
                                    <option value="Lunch">Lunch</option>
                                    <option value="Dinner">Dinner</option>
                                </select>
                            </div>
                            <div className="admin-meal-form-actions">
                                <button
                                    type="button"
                                    onClick={handleMealModalClose}
                                    className="admin-meal-cancel-button"
                                >
                                    Cancel
                                </button>
                                <button
                                    type="submit"
                                    disabled={isLoading}
                                    className="admin-meal-submit-button"
                                >
                                    {isLoading ? 'Saving...' : (isMealEditing ? 'Save Changes' : 'Add Meal')}
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AdminPanel;
