import {useState, useEffect } from "react"

function Complain() {

    const [email, setEmail] = useState('')
    const [message, setMessage] = useState('')
    const [error, setError] = useState('')
    const [selectData, setSelectData] = useState([])
    const [selectValue, setSelectValue] = useState('')

    useEffect( () => {
        fetchData()
    }, [])

    const fetchData = async () => {
        await fetch('https://jsonplaceholder.typicode.com/users')
        .then(res => res.json())
        .then(data => setSelectData(data))
        .catch(err => console.log(err))

    }

    const SelectDropdown = () => {
        return (
            <select value={selectValue} onChange={(e) => setSelectValue(e.target.value)}>
                {
                    selectData?.map((item, index) => (
                        <option value={item.website} key={item.website}>{item.website}</option>
                    ))
                }
            </select>
        )
    }

    const handleSubmit = (e) => {
        e.preventDefault()

        console.log(email + ' | ' + message)

        if (!message) {
            setError(<p className="require">'Message is empty!'</p>)
        }
        else{
            setError('')
        }
    }

    return (
        <>
            <h1>Contact Us</h1>

            <form className="contactForm">
                <label>Email</label>
                <input type='text' id='email' name='email' value={email} onChange={ (e) => setEmail(e.target.value)}/>

                <label>How did you hear about us?</label>
                
                <SelectDropdown/>

                <label>Message</label>
                <textarea id="message" name="message" value={message} onChange={ (e) => setMessage(e.target.value)} >Message</textarea>
                {error}
                <button type="submit" onClick={handleSubmit}>Submit</button>
            </form>
        </>
    )
}

export default Complain
