import {useState, useEffect } from "react"
import axios from "axios"

function Complain() {

    const [email, setEmail] = useState('')
    const [message, setMessage] = useState('')
    const [error, setError] = useState('')
    const [selectData, setSelectData] = useState([])
    const [selectValue, setSelectValue] = useState('')

    useEffect( () => {
        let processing = true
        axiosFetchData(processing)
        return() => {
            processing = false
        }
    }, [])

    // const fetchData = async (processing) => {
    //     await fetch('https://jsonplaceholder.typicode.com/users')
    //     .then(res => res.json())
    //     .then(data => {
    //         if (processing){
    //         setSelectData(data)
    //         }
    //     })
        
    //     .catch(err => console.log(err))

    // }
    

    const axiosFetchData = async (processing) => {
        await axios.get('http://localhost:1240/users')
        .then(res => {
            if (processing){
            setSelectData(res.data)
            }
        })
        
        .catch(err => console.log(err))
    }

    const axiosPostData = async() => {
        const postData = {
            email: email,
            website: selectValue,
            messsage: message
        }

        await axios.post('http://localhost:1240/contact')
        .then(res)
    }
    const SelectDropdown = () => {
        return (
            <select value={selectValue} onChange={(e) => setSelectValue(e.target.value)}>
                {
                    selectData?.map((item) => (
                        <option value={item.website} key={item.website}>{item.website}</option>
                    ))
                }
            </select>
        )
    }

    const handleSubmit = (e) => {
        e.preventDefault()

        console.log(email + ' | ' + selectValue + ' | ' + message)

        if (!message) {
            setError(<p className="require">'Message is empty!'</p>)
        }
        else{
            setError('')
        }
        //process

    }

    return (
        <>
            <h1>Contact Us</h1>

            <form className="contactForm">
                <label>Email</label>
                <input type='text' id='email' name='email' value={email} onChange={ (e) => setEmail(e.target.value)}/>

                <label>How did you hear about us?</label>
                
                <SelectDropdown/>

                <label>Message</label>
                <textarea id="message" name="message" value={message} onChange={ (e) => setMessage(e.target.value)} >Message</textarea>
                {error}
                <button type="submit" onClick={handleSubmit}>Submit</button>
            </form>
        </>
    )
}

export default Complain
