import React, { useState, useEffect } from 'react';
import { Plus, Edit, Trash2, X } from 'lucide-react';
import axios from 'axios';
import "../css/MealAdminPanel.css";

const MealAdminPanel = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [meals, setMeals] = useState([]);
    const [formData, setFormData] = useState({
        name: '',
        description: '',
        calories: '',
        image: '',
        category: 'Breakfast',
        id: null
    });
    const [isLoading, setIsLoading] = useState(false);
    const [isEditing, setIsEditing] = useState(false);

    const categories = ['Breakfast', 'Lunch', 'Dinner'];

    const fetchMeals = async () => {
        try {
            const response = await axios.get('http://localhost:1240/meals');
            setMeals(response.data);
        } catch (error) {
            console.error('Error fetching meals:', error);
        }
    };

    useEffect(() => {
        fetchMeals();
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleEdit = (meal) => {
        setFormData({
            name: meal.name,
            description: meal.description,
            calories: meal.calories,
            image: meal.image,
            category: meal.category,
            id: meal._id
        });
        setIsEditing(true);
        setIsOpen(true);
    };

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this meal?')) {
            try {
                await axios.delete(`http://localhost:1240/delete-meal/${id}`);
                fetchMeals();
            } catch (error) {
                console.error('Error deleting meal:', error);
            }
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);

        try {
            if (isEditing) {
                await axios.put(`http://localhost:1240/update-meal/${formData.id}`, formData);
            } else {
                await axios.post('http://localhost:1240/add-new-meal', formData);
            }
            fetchMeals();
            setFormData({ name: '', description: '', calories: '', image: '', category: 'Breakfast', id: null });
            setIsOpen(false);
            setIsEditing(false);
        } catch (error) {
            console.error('Error saving meal:', error);
        } finally {
            setIsLoading(false);
        }
    };

    const handleModalClose = () => {
        setIsOpen(false);
        setIsEditing(false);
        setFormData({ name: '', description: '', calories: '', image: '', category: 'Breakfast', id: null });
    };

    return (
        <div className="meal-admin-container">
        <div className="meal-admin-panel">
            <div className="meal-admin-header">
                <h2 className="meal-admin-title">Meal Management</h2>
                <button onClick={() => setIsOpen(true)} className="meal-admin-add-button">
                    <Plus size={16} />
                    Add New Meal
                </button>
            </div>

            <div className="meal-admin-grid">
                {meals.map((meal) => (
                    <div key={meal._id} className="meal-admin-card">
                        <img 
                            src={meal.image || "https://via.placeholder.com/150"} 
                            alt={meal.name}
                            className="meal-admin-image"
                        />
                        <div className="meal-admin-content">
                            <h3 className="meal-admin-name">{meal.name}</h3>
                            <span className="meal-admin-category">{meal.category}</span>
                            <p className="meal-admin-description">{meal.description}</p>
                            <div className="meal-admin-calories">{meal.calories} calories</div>
                        </div>
                        <div className="meal-admin-actions">
                            <button onClick={() => handleEdit(meal)} className="meal-admin-edit">
                                <Edit size={16} />
                            </button>
                            <button onClick={() => handleDelete(meal._id)} className="meal-admin-delete">
                                <Trash2 size={16} />
                            </button>
                        </div>
                    </div>
                ))}
            </div>
        </div>

        {isOpen && (
            <div className="meal-admin-modal-overlay">
                <div className="meal-admin-modal">
                    <div className="meal-admin-modal-header">
                        <h3>{isEditing ? 'Edit Meal' : 'Add New Meal'}</h3>
                        <button onClick={handleModalClose} className="meal-admin-close">
                            <X size={20} />
                        </button>
                    </div>
                    <form onSubmit={handleSubmit} className="meal-admin-form">
                        {/* ... existing form fields ... */}
                        <div className="meal-admin-form-actions">
                            <button type="button" onClick={handleModalClose} className="meal-admin-cancel">
                                Cancel
                            </button>
                            <button type="submit" disabled={isLoading} className="meal-admin-submit">
                                {isLoading ? 'Saving...' : (isEditing ? 'Update Meal' : 'Add Meal')}
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        )}
    </div>
);
};

export default MealAdminPanel;
